import styles from './Carousel.css';
const getPositionByIndex = ({ currentSlide, slideIndex, slideLength, }) => {
    if (currentSlide === slideIndex + 1 ||
        currentSlide === slideIndex - (slideLength - 1))
        return -1;
    if (currentSlide === slideIndex)
        return 0;
    if (currentSlide === slideIndex - 1 ||
        currentSlide === slideIndex + (slideLength - 1))
        return 1;
    return 2;
};
export const definePositionStyle = ({ currentSlide, slideIndex, slideLength, isWide, }) => {
    const getPosition = getPositionByIndex({
        currentSlide: slideIndex,
        slideIndex: currentSlide,
        slideLength,
    });
    if (getPosition === -1)
        return ['uil-d-block', styles.getRightSlide(isWide)];
    if (getPosition === 0)
        return ['', styles.middleSlide];
    if (getPosition === 1)
        return ['uil-d-block', styles.getLeftSlide(isWide)];
    return ['md:uil-d-none', null];
};
