import { jsx } from '@emotion/react';
import AlgoliaLogo from '../../logos/AlgoliaLogo';
import AlgoliaLogoWhite from '../../logos/AlgoliaLogoWhite';
const NavLogo = ({ logo, theme, onLogoContextMenu }) => {
    const logoClasses = 'uil-d-none lg:uil-h-25 lg:uil-w-auto lg:uil-d-block';
    if (logo) {
        return (jsx("img", { src: logo.src, width: "485", height: "120", alt: logo.alt, className: logoClasses, onContextMenu: (e) => {
                onLogoContextMenu(e);
            } }));
    }
    if (theme === 'light') {
        return (jsx(AlgoliaLogo, { className: logoClasses, onContextMenu: onLogoContextMenu }));
    }
    return (jsx(AlgoliaLogoWhite, { className: logoClasses, onContextMenu: onLogoContextMenu }));
};
export default NavLogo;
